import React, { useState } from "react";
import SemanticDatepicker from "react-semantic-ui-datepickers";
import "react-semantic-ui-datepickers/dist/react-semantic-ui-datepickers.css";
import { Button, Form, Grid, Header, Image, Segment } from "semantic-ui-react";

export default function AddNoteForm(props) {
  const [currentDate, setNewDate] = useState(null);
  const [invalidForm, setInvalidForm] = useState(true);
  const [state, setState] = useState({
    title: "",
    date: "",
    location: "",
    description: "",
  });

  const AppWithBasic = () => {
    const [currentDate, setNewDate] = useState(null);
    const onChange = (event, data) => setNewDate(data.value);
    return <SemanticDatepicker onChange={onChange} />;
  };
  const AppWithRangeAndInPortuguese = () => {
    const [currentRange, setNewRange] = useState([]);
    const onChange = (event, data) => setNewRange(data.value);
    return <SemanticDatepicker locale="pt-BR" onChange={onChange} type="range" />;
  };



  function handleChange(e) {
    console.log(e.target.value, 'this is handlechange')
    setState({
      ...state,
      [e.target.name]: e.target.value,
    });
  }


  function handleSubmit(e) {
    console.log(state, "this is sate from add note form");
    e.preventDefault();
    console.log("handleSubmit fired");
    props.handleAddNote(state);
  }

  return (
    <Grid textAlign="center" style={{ height: "25vh" }} verticalAlign="middle">
      <Grid.Column style={{ maxWidth: 450 }}>
        <Segment>
          <Form autoComplete="off" onSubmit={handleSubmit}>
            <Form.Input
              className="form-control"
              name="title"
              value={state.title}
              placeholder="Title"
              onChange={handleChange}
              required
            />
            <Form.Input
              control={SemanticDatepicker}
              className="form-control"
              name="date"
              value={state.date}
              placeholder="When"
              onChange={handleChange}
            />

            <Form.Input
              className="form-control"
              name="location"
              value={state.location}
              placeholder="Location"
              onChange={handleChange}
            />
            <Form.Input
              className="form-control"
              name="description"
              value={state.description}
              placeholder="Description"
              onChange={handleChange}
            />
            <Button type="submit" className="btn">
              ADD Note
            </Button>
          </Form>
        </Segment>
      </Grid.Column>
    </Grid>
  );
}
