import React, { useState, useEffect } from 'react';
import {  Grid } from 'semantic-ui-react';
import * as notesApi from '../../utils/notes-api';
import PageHeader from '../../components/Header/Header';
import NoteFeed from '../../components/NoteFeed/NoteFeed';

export default function NoteListPage({user, handleLogout,notes}){

  const [allNotes, setAllNotes] = useState([]);

  console.log(allNotes,'this is allNotes')
  async function getNotes(){ 

    console.log(allNotes,'this is allNotes')
    try {
      const data = await notesApi.getAll();
      // console.log(data,'this is data')
      // console.log(allNotes,'this is allNotes')
      setAllNotes([...data.allNotes])
    } catch(err){

      console.log(err, ' this is the error')
    }
  }

  useEffect(() => {
    getNotes()
  }, [])


  return (
      <Grid centered >
        <Grid.Row>
          <Grid.Column>
            <PageHeader user={user} handleLogout={handleLogout}/>
          </Grid.Column>
        </Grid.Row>
        <Grid.Row>
          <Grid.Column style={{maxWidth: 450}}>
            <NoteFeed
              user={user} 
              numPhotosCol={1} 
              notes={notes}/>
          </Grid.Column>
        </Grid.Row>
    </Grid>
    )
}
