import tokenService from './tokenService';

const BASE_URL = '/api/notes';

export function create(note){
  console.log(note, 'in create')
  return fetch(BASE_URL, {
    method: 'POST',
    body: note,
    headers: {
      'Authorization': 'Bearer ' + tokenService.getToken()
    }
  }).then(res => res.json())
}

export function getAll() {
  return fetch(BASE_URL, {
    headers: {
      'Authorization': 'Bearer ' + tokenService.getToken()
    }
  })
  .then(res => res.json());
}
